return (
<div className='textinput-area' onSubmit={(e)=> submit(e)}>
      <form >
        <div className="header-input"><h1>LOGIN</h1></div>
        
        <div className='divider'></div>
        <input type="text" placeholder='Enter email' name='email' value={userCred.email} onChange={handleF}/>
        <div className='divider'></div>
        <input type="password" placeholder='Enter password' name='password' value={userCred.password} onChange={handleF}/>
        <div className='divider'></div>
        <button className='login-button'>Login</button>
        {error && <p>{error}</p>}


      </form>
    </div>
  )
}

export default TextinputLogin
