import axios  from "axios";
import React, { useState } from 'react'


import './TextinputLogin.css'


const url = 'http://localhost:7000/signin'


function TextinputLogin() {
  const [userCred , setuserCred] = useState({
    email:'',password:'',
  });

const [error , seterror] = useState(null);

function handleF(e){
  const newdata = {...userCred}
  newdata[e.target.name] = e.target.value
  setuserCred(newdata)
  console.log(newdata)
}

function submit(e){
  e.preventDefault();
  axios.post(url,userCred)
  .then(res=>{
    console.log(res.statusText);
    this.props.history.push('/')
    seterror(null);
  }).catch(res=>{
    seterror(res['response']['data']['msg']);
  })
}



return (
<div className='textinput-area' onSubmit={(e)=> submit(e)}>
      <form >
        <h1>LOGIN</h1>
        <div className='divider'></div>
        <input type="text" placeholder='Enter email' name='email' value={userCred.email} onChange={handleF}/>
        <div className='divider'></div>
        <input type="password" placeholder='Enter password' name='password' value={userCred.password} onChange={handleF}/>
        <div className='divider'></div>
        <button className='login-button'>Login</button>
        {error && <p>{error}</p>}


      </form>
    </div>
  )
}

export default TextinputLogin
