import axios from "axios";
import { createContext, useContext, useEffect, useState } from "react";
import { useCookies } from "react-cookie";

const LoginContext = createContext({});

const url = 'http://localhost:7000/signin'

export  function LoginContextprovider({children}){
    const [userstate , setuserstate] = useState(null);
    const [cookie , setcookie] = useCookies(null);
    useEffect(()=>{
    }, [cookie, userstate])

     function inituser(props){
         setuserstate(props)
    }
    
      function setToken(props){
         setcookie('token', props,{path:'/'});
}
    
    return (
        <LoginContext.Provider value={{userstate,inituser,setToken,cookie}}>
        {children}
        </LoginContext.Provider>
    )
}

async function  submit(e) {
    e.preventDefault();
    const res = await axios.post(url,userCred)
    seterror(null);
    setToken(res['data']['token'])
    inituser(res['data'])
    console.log(userstate)
    navi('/')
  }

export function useUserAuth(){
    return useContext(LoginContext);
}
