import React, { useEffect, useState } from 'react'

import './Predictinput.css'

let Predictinput = () => {
    const [data , changedata] = useState({
      locationLis:[]
    });
    const [formdata , changefdata] = useState({
      bhk:'', sqft:'', location:'',bath:''
    });



   
useEffect(()=>{
  fetch('http://127.0.0.1:8000/get/getlocation',{method:'GET',mode:'cors'}).then((res) => res.json()).then((data)=> changedata(data));
},[]);

function handleForm(e){
  e.preventDefault();
  const newdata = {...formdata}
  newdata[e.target.name] = e.target.value
  changefdata(newdata)
}

function predictfun(){
  let reqstr = ''
  reqstr = formdata['bhk']+'-'+formdata['sqft']+formdata['location']+'-'+formdata['bath'];
  console.log(reqstr)
}

const loclist = data['loc']?.map(
  (e)=><option id={e} >{e}</option>
);

  return (

    <div className='main-area'>
      <div className='textinput-area'>
      <form >
        <h1>Enter details from prediction.</h1>
        <div className='divider'></div>
        <input type="text" placeholder='Enter BHK' name='bhk' id='bhk' value={formdata.bhk} onChange={handleForm}/>
        <div className='divider'></div>
        <input type="text" placeholder='Enter Square Feet' name='sqft' id='sqft' value={formdata.sqft} onChange={handleForm}/>
        <div className='divider'></div>
        <select name='location' className='dropdown' value={formdata.location} onChange={handleForm}>
          {loclist}
        </select >
        <div className='divider'></div>
        <input type="text" placeholder='Enter Bathrooms' name='bath' id='bath' value={formdata.bath}  onChange={handleForm}/>
        <div className='divider'></div>
        <button className='login-button' onClick={predictfun}>Predict</button>
      </form>
      </div>
      </div>
      ) 

}
export default Predictinput
