import React from "react";
import "../pricegraph/pricegraph.css";
import {
  Chart as ChartJS,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
  PointElement,
  Filler,
  LineElement,
  Title,
} from "chart.js";
import { Bar, Line } from "react-chartjs-2";

ChartJS.register(
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
  PointElement,
  Filler,
  LineElement,
  Title
);

const Pricegraph = (data) => {
  console.log(data["data"]);
  let key = [];
  let val = [];
  for (let v of Object.keys(data["data"]["allLocCount"])) {
    key.push(v);
    val.push(data["data"]["allLocCount"][v]);
  }
  const datagraph = {
    labels: key,

    datasets: [
      {
        label: "Prediction distribution.",
        data: val,
        backgroundColor: ["#3e95cd", "#8e5ea2", "#3cba9f", "#e8c3b9"],
        borderColor: "black",
        borderWidth: 1,
      },
    ],
  };

  const linedata = {
    labels:key,
    datasets: [
      {
        label: "Location which has highest estimate",
        data: data["data"]["pricestat"]["all"],
        backgroundColor: "yellow",
        borderWidth: 1,
        borderColor: "green",
        tension: 0.4,
        fill: true,
        pointStyle: "rect",
        pointBorderColor: "blue",
        pointBackgroundColor: "#fff",
        showLine: true,
      },
    ],
  };
  const options = {};
  return (
    <div className="bargraph">
      <h1>User statstics</h1>
      
      <Bar data={datagraph} options={options}></Bar>
      <div className="dividergraph"></div>
      <Line data={linedata} options={options}></Line>
    </div>
  );
};

export default Pricegraph;
