import React from "react";
import "../pricegraph/pricegraph.css";
import {
  Chart as ChartJS,
  BarElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar, Line } from "react-chartjs-2";

ChartJS.register(BarElement, CategoryScale, LinearScale, Tooltip, Legend);

const Pricegraph = (data) => {
  console.log(data["data"]);
  let key = [];
  let val = [];
  for (let v of Object.keys(data["data"]["allLocCount"])) {
    key.push(v);
    val.push(data["data"]["allLocCount"][v]);
  }
  const datagraph = {
    labels: key,

    datasets: [
      {
        label: "Prediction distribution.",
        data: val,
        backgroundColor: ["#3e95cd", "#8e5ea2", "#3cba9f", "#e8c3b9"],
        borderColor: "black",
        borderWidth: 1,
      },
    ],
  };

 const  linedata = {
    labels:["Jan","Feb", "March", "April", "May", "June", "July", "August", "September", "Oct", "Nov", "Dec"],
    datasets:[
      {
        label:"First Dataset",
        data:[10, 20, 30, 42, 51, 82, 31, 59, 61, 73, 91, 58],
        backgroundColor:'yellow',
        borderColor:'green',
        tension:0.4,
        // fill:true,
        // pointStyle:'rect',
        // pointBorderColor:'blue',
        // pointBackgroundColor:'#fff',
        showLine:true
      }
    ]
  };
  const options = {};
  return (
    <div className="bargraph">
      <h1>User statstics</h1>
      <Bar data={datagraph} options={options}></Bar>
      <Line data={linedata} options={options}>hh</Line>
    </div>
  );
};

export default Pricegraph;
