import React, { useEffect, useState } from 'react'

import './Predictinput.css'

let Predictinput = () => {
    const [data , changedata] = useState({
      locationLis:[]
    });
    const [formdata , changefdata] = useState({
      bhk:'', sqft:'', location:'',bath:''
    });



   
useEffect(()=>{
  fetch('http://127.0.0.1:8000/get/getlocation',{method:'GET',mode:'cors'}).then((res) => res.json()).then((data)=> changedata(data));
},[]);

function handleForm(e){
  e.preventDefault();

}

const handleInput = (e)=>{

}




const loclist = data['loc']?.map(
  (e)=><option id={e} className='location' value={formdata.location} >{e}</option>
);

  return (

    <div className='main-area'>
      <div className='textinput-area'>
      <form onSubmit={handleForm}>
        <h1>Enter details from prediction.</h1>
        <div className='divider'></div>
        <input type="text" placeholder='Enter BHK' className='bhk' id='bhk' value={formdata.bhk}/>
        <div className='divider'></div>
        <input type="text" placeholder='Enter Square Feet' className='sqft' id='sqft' value={formdata.sqft}/>
        <div className='divider'></div>
        <select name='location' className='dropdown'>
          {loclist}
        </select >
        <div className='divider'></div>
        <input type="text" placeholder='Enter Bathrooms' className='bath' id='bath' value={formdata.bath} />
        <div className='divider'></div>
        <button className='login-button'>Predict</button>
      </form>
      </div>
      </div>
      ) 
}

export default Predictinput
