  const [Cookie, setCookie] = useCookies();

  async function loaddetails(){
    const resfianl = await fetch(
      'http://localhost:7000/getdetailssaved',
      {
        method: "POST",
        headers: {
          'Content-Type': "application/json"
        },
        body: JSON.stringify({"email":Cookie['email']})
      }
    );
    const data = await resfianl.json();
    setpropdetails(data)  
  }

  useEffect(()=>{
    loaddetails();
  },[]);
  

  return (
    <>
    <Navbar />
    <div className="maindiv-about">
      <div className="About-main">
        <input value={budget}
          onChange={(e)=>{handleB(e)}} placeholder="Enter budget"   ></input>
        <div className="main-hub"><Propertycardbudget/></div>
      </div>
      <div className="graph"><Pricegraph  ></Pricegraph></div>
      </div>
    </>
  );
};

export default About;
